#Create Alphabet Class
class Alphabet:
    def __init__(self, chosenAlphabet):
        self.alphabet = chosenAlphabet
    def change(self, text):
        splitString = list(text)
        output = []
        for character in splitString:
            if character == " " or character == "_" or character == "." or character == "," or character == ":" or character == "?" or character == "(" or character == ")" or character == "!" or character == "+" or character == "-" or character == "$" or character == "@":
                output.append(character)
            else:
                output.append(self.alphabet[normalAlphabet.index(character)])
        return output

