#Convert Normal Text To Styled Text
def convertText(text):
        try:
            result = [
                sep.join(gothic.change(text)),
                sep.join(circle.change(text)),
                sep.join(negCircle.change(text)),
                sep.join(monospace.change(text)),
                sep.join(doublestuck.change(text)),
                sep.join(cursive.change(text)),
                sep.join(squared.change(text)),
                sep.join(negSquared.change(text)),
                sep.join(serif.change(text)),
                sep.join(curvy.change(text)),
                sep.join(boxed.change(text)),
                sep.join(fancyBoxed.change(text)),
                sep.join(weird.change(text)),
                sep.join(struck.change(text)),
                sep.join(asian.change(text)),
                sep.join(circleBox.change(text)),
                sep.join(wavy.change(text)),
                sep.join(grid.change(text)),
                sep.join(rustify.change(text)),
                sep.join(symbols.change(text)),
                sep.join(strokes.change(text)),
                sep.join(heiroglyph.change(text)),
                sep.join(italic.change(text)),
                sep.join(bold.change(text)),
                sep.join(sorcerer.change(text)),
                sep.join(number.change(text)),
                sep.join(small.change(text)),
                sep.join(upsidedown.change(text)),
                sep.join(antrophobie.change(text)),
                sep.join(handWriting.change(text)),
                sep.join(fancier.change(text)),
                sep.join(emoji.change(text)),
                sep.join(parenthesized.change(text)),
                sep.join(stroked.change(text)),
                sep.join(ethiopic.change(text)),
                sep.join(blocked.change(text)),
                sep.join(symbolic.change(text))
            ]
            return result
        except:
            return 'error'
